
public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10, b = 20;
		int c = a & b;
		int d = a | b;
		int e = a ^ b;
		int f = ~a;
		System.out.println(a << 30);// left shift
		System.out.println(a >> 2);// right shift
		System.out.println(a >>> 2);// right shift with zero fill
	}

}
